<script lang="ts">
	import { IDBService } from "$lib/workers/db/service";

	import { showAddToPlaylistPopper } from "$lib/stores/stores";
	import List from "./List.svelte";

	import type { IDBPlaylist } from "$lib/workers/db/types";
	import { createEventDispatcher } from "svelte";
	import Modal from "../Modal";
	import CreatePlaylist from "./CreatePlaylist.svelte";
	$: isShowing = $showAddToPlaylistPopper.state;

	$: item = $showAddToPlaylistPopper.item;
	let showCreatePlaylist = false;
	let playlists: IDBPlaylist[] = [];
	const fetchPlaylists = async () => {
		const promise = await IDBService.sendMessage("get", "playlists");
		if (promise) {
			playlists = promise;
		}
	};
	$: if (isShowing) fetchPlaylists();
	const dispatch = createEventDispatcher();
	let hasFocus = false;
	// $: console.log(playlists, showConfirmation, item);
</script>

{#if showCreatePlaylist}
	<CreatePlaylist
		hasFocus={true}
		on:close={() => {
			hasFocus = true;
			showCreatePlaylist = false;
		}}
		on:submit={async (e) => {
			await IDBService.sendMessage("create", "playlist", {
				name: e.detail?.title,
				description: e.detail?.description,
				thumbnail:
					e.detail?.thumbnail ??
					"data:image/svg+xml;base64,",
				items: Array.isArray(item) ? [...item] : [item],
			});
			hasFocus = true;
			showCreatePlaylist = false;
			dispatch("close");
		}}
	/>
{/if}

{#if isShowing}
	<Modal
		zIndex={1000}
		on:close={() => {
			dispatch("close");
			hasFocus = true;
		}}
		bind:hasFocus
	>
		<h1 slot="header">Add to Playlist</h1>
		<div class="list">
			<List
				on:click={async (e) => {
					const playlist = playlists[e.detail];
					const items = [...playlist?.items].concat(item);
					const promise = await IDBService.sendMessage("update", "playlist", {
						hideAlert: false,
						id: playlist.id,
						items: items,
					});
					showAddToPlaylistPopper.set({ state: false, item: undefined });
				}}
				items={playlists}
				on:create={() => {
					hasFocus = false;
					showCreatePlaylist = true;
				}}
			/>
		</div>
	</Modal>
{/if}

<style lang="scss">
	.list {
		overflow-y: auto;
		position: relative;
		padding: 0 0.5rem;
		// justify-content: center;justify-content
	}

	.playlist-modal {
		position: absolute;
		// gap: 0.8rem;gap
		padding: 0.8rem;
		z-index: 100;
		background: var(--mobile-popper-bg);
		display: flex;
		flex-flow: column nowrap;
		top: 50%;
		left: 50%;
		min-width: 30%;
		width: 100%;
		min-height: 50%;
		max-width: 100%;
		max-height: calc(100vh - 4rem);
		border-radius: $lg-radius;
		border-color: rgb(129 129 129 / 41.1%);
		border-width: 0.025rem;
		border-style: solid;
		transform: translate(-50%, -50%);

		@media screen and (min-width: 640px) {
			max-width: 35%;
			max-height: 65%;
		}
	}
</style>
